# Kimberly Lowfrom pd import *import randomrandom.seed() class mystrategy(player):	def __init__(self,name="mystrategy"):		player.__init__(self,name)	def strategy(self,turn):		if turn == 0:			return DEFECT		if self.rival_history(turn - 1) == COOPERATE:			if self.my_history(turn - 1) == COOPERATE:				return COOPERATE			if self.my_history(turn - 1) == DEFECT:				return DEFECT		if self.rival_history(turn - 1) == DEFECT:			if self.my_history(turn - 1) == DEFECT:				return DEFECT			if self.my_history(turn - 1) == COOPERATE:				return DEFECT